INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1380, '胡里山海滨浴场', '胡里山海滨浴场位于厦门市的南面，距市区约8公里，是厦门市比较热门的海滨浴场之一。这里潮暖、滩平、沙细、水良、地热资源丰富。游人在宽阔平坦的沙滩上可享受拾贝、捉鱼、抓蟹等乐趣。', '', '', '', '', '', '', '', '', '无需门票。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1389, '小池培斜萌宠动物园', '小池培斜萌宠动物园共引进“萌宠”小动物10余种，有风靡全球的神兽草泥马（羊驼、美丽而骄傲的孔雀、憨态可掬的海狮、活泼可爱的香猪、灵活好动的金太阳鹦鹉…小动物应有尽有，动物园设有鹦鹉观赏互动区域、小动物喂食区域、动物观赏与体验区域等，在这里，游客们除了近距离观赏“萌宠”小动物外，还可以与萌宠们现场互动喂食、合影留念。', '', '', '', '', '', '', '', '', '60元', 3);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1410, '白门寺', '白门寺位于福州市长乐市猴屿。包括祠堂皆是敬顺仰止之地，得妙法真如之地，当顶礼。寺庙庄严，神圣不可侵犯，寸土之间，可随顺而不可随意更改，敬顺即得妙法。', '', '', '', '', '', '', '', '', '', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1412, '安溪普陀寺', '普陀寺普陀寺位于福建省安溪县凤山，始建于宋· 建隆二年（公元961年），初名“龙安”，后改名“善惠”，开山立寺至今已有一千多年的历史。寺名由南海普陀山方丈戒忍法师亲笔题名，并且从浙江普陀山上迎请到“南海观音”永久供奉。过去曾有多位长老大德在此精进礼佛参禅、 弘扬佛法、 普渡众生。建筑选用仿唐风格，在已落成的圆通宝殿内，供奉着青铜塑造的男相千手千眼观世音菩萨一尊，高8.68米，重达16吨。寺内还有一块劝农摩崖石刻，是安溪县文物保护单位。石刻上面以楷书刻着：“住山刊事来，秋日劝农职，宣和辛丑初，前田吴公权，龙当黄宗之。”劝农是唐宋一种特有的现象，石刻上的内容，讲到山上僧侣和劝农的关系，记载宋代官员来寺院劝农事宜，刊刻时间为宋宣和辛丑年（公元1121年）。', '', '', '', '', '', '', '', '', '', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1414, '龙潭山水风景区', '龙潭景区浓缩了灵石山森林公园的精华，真正是“无处不树木，遍地是山花”，放眼望去，草木萌动，万木争荣，桫椤、银杏、金钱松、三尖杉、福建柏、香樟树等，应有尽有，墨绿无边，这些枝繁叶茂的古名树，其树龄均在数百年以上。景区由龙潭坑与戏龙潭两部分组成，戏龙潭以瀑布和险峻的山势为特点，龙潭坑则以怪石和清幽的潭水见长。景区以两条叮咚的溪水为风景骨架，融流泉飞瀑、峋嶙怪石、清冽深潭为一体，点缀山花奇草，展示丰茂的南亚热带雨林和常绿阔叶林景观。游览景区一般从停车场起步，经日潭、月潭、星潭、洗心池、大蟒树到溪光亭，这里有条三叉路口，向右经窥龙洞到戏龙潭，直通往千年古寺灵石寺；向上经鹊桥、待月亭、龙潭坑、天书亭到天书岩，这里又是一处三叉路口，向右可到蛟龙出洞，向上经断崖谷风、卧仙石、一线天到海拔963米的留雪峰。', '', '', '', '', '', '', '', '', '包含于灵石山国家森林公园景区，通用于灵石山国家森林公园景区门票内。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1417, '西河游泳场', '闽江西河天然游泳场位于福州市鼓楼区洪山桥下游闽江北港北岸，总面积6.48万平方米，泳区面积5.38万平方米，可同时容纳万名以上游泳爱好者在此休闲健身，是目前全国最大的城市内河游泳场，为榕城市民提供了又一个休闲游乐的好去处。在游泳场岸边平台上，已建成两个标准篮球场，四周加上了围栏。围栏外还有相应的健身器材。有正式的瞭望塔，是西河游泳场的标志性建筑之一，并配有高音喇叭和探灯，更好地保障游泳者的安全。现建成标准淋浴房和卫生间。另外，整个西河游泳场已按公园建设的标准进行了景观绿化、美化，建设户外观景平台等。', '', '', '', '', '', '', '', '', '游泳是免费的，但寄存、淋浴等需要收费。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1422, '翠园', '翠园是在平潭县是一处比较老的休闲公园，可以看到有情侣在公园里面散步。', '', '', '', '', '', '', '', '', '免费开放。公园不需要门票，公园内游泳馆25元', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1424, '中国长乐海蚌公园', '长乐海蚌公园位于长乐漳港镇，与福州长乐国际机场毗邻。海蚌公园为福州近郊商业气息不浓，基本为未开发的海滨浴场。海蚌公园面向台湾海峡，沙滩还算干净。公园另外还建设有主题公园一处，主题公园内有各类海蚌造型的雕像群及美人鱼石像供游客游览，还有设计奇特造型可爱的石凳给你提供一个休息处。', '', '', '', '', '', '', '', '', '免费开放。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1426, '福建省革命历史纪念馆', '福建省革命历史纪念馆，座落于福州著名风景名胜区鼓山脚下，纪念馆占地30亩，建筑面积16000平方米。整座建筑融中西建筑风格为一体，以内广场为中心，组织起序厅、陈列厅、半景画馆、办公楼、培训中心等空间序列。馆体建筑具有福建土楼的风格。序厅大型影雕《闽之魂》，高2.4米，长14米，黑色花岗岩上镌刻着福建百年风云人物和革命文物精华，气势磅礴，栩栩如生，堪称福建工艺美术经典之作。《福建革命史陈列》和《解放福建》半景画馆是纪念馆的基本陈列。《福建革命史陈列》，面积约1800平方米，展出革命文物800多件和历史照片800多幅。陈列采用文物、版面展示与历史场景相结合的形式，配以声、光、电等现代化手段，全面、生动地展示了福建自1840年鸦片战争到1949年新中国成立的百年历史风云。《解放福建》半景画，长80米，高17米，采用无接缝画布绘制战争场景，在180度半圆形视角空间里，结合大型地面塑型和声光电等高科技演示，生动艺术地再现了1949年解放福州、厦门海陆战争的激烈战斗场面，气势磅礴，宏伟壮观，具有强烈的震撼力、感染力和表现力，使观众有亲临其境的感觉。《解放福建》半景画馆是目前全国最大的半景画馆，也是福建文化艺术史上的首创。', '', '', '', '', '', '', '', 'http://www.fj1116.cn/', '纪念馆是免费开放，其中半景画馆实行收费参观，门票15元，学生、退休人员等半票。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1428, '青云山云天石廊', '云天石廊峡谷景区（行程约3.5小时）：登云天石廊——青云山主峰状元峰：观宋代状元萧国良“状元祖屋”遗址，游双龙瀑布、情侣瀑布、飞瀑三潭、雄狮吼天、通天大王庙、拜仙台、状元晒靴、鹿鸣谷、灵芝崖、羚羊洞、天门洞、仙人洞等景点。石廊峡谷景区以峡谷和石廊为名，云天石廊是石廊峡景区的主要景观，位于青云峰近山顶处，又称“登天廊”，是软硬岩层被风化后而形成的4层长条形通道，远可观，近可行。永泰青云山风景名胜区位于福建省永泰县的岭路乡，景区内山高林茂、云雾飘渺，多奇岩幽谷、碧泉飞瀑。', '', '', '', '', '', '', '', '', '27元', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1430, '龙水湖旅游度假区', '龙水湖温泉坐落于龙水湖国际旅游度假区，背靠35平方公里的玉龙山国家森林公园，依托5300亩龙水湖的水利风景区，是集观光、体验、疗养为一体的综合型旅游度假区。龙水湖温泉主要由温泉中心、温泉泡池区、大型主题水上乐园三大部分构成。以优越的山水自然环境为生态基底，以佛教文化为灵魂，以养生度假为重点，打造具有独特文化气质的养生圣地。除了冬季泡温泉外，大型水上乐园则是项目夏季运营的核心。将成为川渝地区很有特色的水上乐园及国内第一规模的滨湖全季节双乐园温泉水世界。龙水湖温泉水源自三叠纪地下岩层1608米，日出水量6000立方米， 出水温度 62 摄氏度，PH值7.5，呈弱碱性，富含偏硅酸、偏硼酸、偏磷酸、游离二氧化碳、锌、锶、钙、镁、铁、钠、钾、硫等30多种微量元素。对人体神经系统、循环系统、内分泌系统、运动系统、心血管系统、表皮组织均有一定的治疗和保健作用。', '', '', '', '', '', '', '', 'http://www.lslake.com/', '景区免费开放，景区内游玩项目需另买票，其中温泉198元，帆船70元，观光船50元，飞跃丛林亲子线60元', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1432, '芙蓉江', '芙蓉江发源于贵州省绥阳县的石瓮子，由南向北流经黔渝两省市，在武隆江口注入乌江，全长231公里。芙蓉江古名濡水，又名盘古河，因与乌江交汇处的江口镇沿岸多芙蓉树，故称芙蓉江。芙蓉江重庆段属重点风景名胜区，河道长为35公里，以典型的规模宏大的“U”形峡谷为主，主要景点有芙蓉洞景区；盘古河景区的苗寨风情、古木竹海、猴园、芭蕉林；悬坝景区的蝴蝶谷、龙孔飞瀑、大小石笋；珠子溪景区的红军渡、玛瑙飞泉、姜太公吊鱼、珠子溪。景区内有国家珍稀动物黑叶猴，可贴近观察，猕猴更是成群可见。可持长篙，驾轻舟，泛流于碧波之中，领略大自然之神奇美妙。', '', '春、夏、秋最合适，江水碧蓝，微风习习，山坡葱郁，风景最美。', '汽车从武隆汽车站乘坐发往江口镇的班车，到江口后直接转乘，全程9元/人，四十分钟左右即到。', '', '', '', '', '', '游轮:120人民币；过江速滑:80人民币 (1月1日-12月31日 周一-周日)', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1433, '青龙湖', '青龙湖位于璧山县西北29公里处的云雾山中，海拔400—800米。景区多云雾，夏短春长，季节温差小，景区面积50多平方公里，湖面15公顷，湖水清澈，飞湍泻瀑，港汊通幽；湖岸植被繁茂，青翠可人。', '', '四季皆宜。', '公交在重庆陈家坪汽车站乘坐汽车经旧成渝路往壁山方向在福里树村下车，然后换乘当地汽车，可直达景区，时间40分钟，10元/人。自驾由内环—渝遂高速—大路下道—x373（县道）—青龙湖。', '', '', '', '', '', '无需门票', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1434, '海棠晓月温泉', '海棠晓月养生泉位于重庆市南岸区南坪东路海棠晓月，地处巴渝十二景之一“海棠烟雨”原址，温泉采自地下3000米深处的深层热水，属硫酸钙镁型，水温52.8℃，日流量2500吨，拥有52个天然温泉泡池。海棠晓月养生泉建立在昔日的古巴渝十二景之一“海棠烟雨”旧址上，北临长江，背倚南山，步行至著名的“重庆外滩”南滨路仅需三分钟，环境优美，交通便利，是重庆市区唯一的温泉休闲度假圣地。', '【活动票】海棠晓月温泉通票¥118¥59.9起查看报价海棠晓月圣地温泉成人票¥198¥59.9起查看报价海棠晓月圣地温泉儿童票¥100¥49起查看报价海棠晓月温泉门票亲子票（1大1小）¥176¥88起查看报价成人套票（温泉票+套餐）¥198¥118起查看报价儿童套票（温泉票+套餐）¥138¥88起查看报价', '', '公交乘坐304路、315路、318路、321路、323路、346路、347路、352路、353路等公交车至【海棠晓月】站下车，步行前往即至。', '', '', '', '', '', '128元', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1435, '长寿古镇', '长寿古镇位于重庆市长寿区，镇内有人文大街、万寿广场、魁星双塔、万寿公园等许多仿古建筑，这里可以感受到浓郁的巴渝文化和中国寿文化。在古镇内可以体验一下小镇的慢生活，走走逛逛，看看小桥流水、嬉戏的鱼儿，或是和几个好友喝喝茶，聊聊天，畅谈人生，很是悠闲自在。古镇的米粉和肥肠饭味道不错，感兴趣的游客可以尝尝。', '长江三峡3日顺道游 天天发 三峡大坝+神农溪+白帝城+小三峡¥820¥820起查看报价长江三峡2日顺道游 天天发 三峡大坝+神农溪+屈原故里小三峡¥580¥580起查看报价', '四季皆宜', '公交1、从重庆火车站北站出发，到达长寿站后直接坐103路公交，长寿火车站只有103路公交车，这条公交终点站就是古镇。2、坐汽车到长寿，在朝天门或者红旗河沟坐到长寿的汽车，在长寿汽车总站下，然后在站外坐103路公交，这里离古镇已经不远。打的也就10块钱左右。', '', '', '', '', 'http://ptgz.tyjt.cc/', '无需门票', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1436, '816地下核工程', '816军工洞体位于涪陵区白涛街道（原白涛镇），完全隐藏在金子山山体内。该军工洞曾经是一个核工厂实体（未生产，无辐射），洞体总长约20余公里，洞内建成大型洞18个，道路、导洞、支洞、隧道、竖井多达130多条。1966年9月，在此建立中国第二个核原料工业基地——816工程。1967年正式动工，历经17年建设，先后投入6万多建设大军。1984年工程接近收尾，但随着国际形势和国民经济战略调整的需要，被紧急叫停，2004年国家批准解密。 816军工洞体有上下三层干支洞体体系，总长达20余公里，洞室内楼层功能分布明显，隔离性强，有如迷宫。洞中还建有9层高楼房，安置有电梯，其洞体设计和建设代表了当时洞体建筑设计、工程建设的较高水平。洞内旅游资源有：816核军工洞体、核工厂主洞、核反应堆大厅、核废料处理场、“锅底”、中央控制室、排风塔等。目前已开放的只有核反应大厅。', '', '', '', '', '', '', '', '', '70元', 0);
