INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1509, '北港村石头厝', '走进位于平潭岛东北角的北港村，典型的平潭古石厝错落有致地分布着，建筑主体多为青、灰色，排列整齐却色彩斑斓的瓦片给古朴的民居增添了几分灵气。配以碧蓝的海水，层次分明的花田，宛如一幅素雅的水墨画。北港村是一个传统的小渔村，背山面海，全村400多户，绝大多数青壮年外出打工，年长者则靠出海捕鱼为生。两年前，台湾团队的入驻，让这里古老的石头厝焕发出新生机。', '暑期出行季福州出发平潭精华纯玩2日游 一价全含（车+导+房）¥499¥368起查看报价<纯玩>平潭岛+北港村+拉网捕鱼+民宿/酒店2日游¥488¥399起查看报价平潭岛北港+沙滩露营经典巴士2日游（纯玩）¥399¥368起查看报价9月狂欢平潭岛沙滩party烧烤+北港+宿海景管屋经典2日游¥488¥399起查看报价醉美福建福州三坊七巷+平潭海坛古城+烧烤露营经典3日游¥588¥488起查看报价品质纯玩福州三坊七巷+平潭沙滩party+烧烤民宿3日游¥688¥588起查看报价玩足7小时平潭大福湾+海坛古城+北港村+龙凤头深度纯玩一日游¥258¥138起查看报价享游金秋！平潭岛大福湾+海坛古城+龙凤头汽车超值纯玩1日游¥258¥90起查看报价享游金秋！福州出发平潭岛海坛古城+龙凤头+北港+长江澳一日游¥258¥108起查看报价', '', '', '', '', '', '', '', '', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1512, '闽江公园', '闽江公园，位于福建省福州市区闽江南、北两岸，它是福州市内最大的开放式休闲公园。闽江公园分为北园和南园，以江为界，遥相呼应。其中，北园和南园分别于2000年5月1日和2002年5月1日建成并向市民和游客免费开放。北园全长5．5公里，面积44．24公顷，集亲水性、生态性、艺术性为一体，体现了独特的闽江流域文化特色和榕城风情。她由9个景区组成，自西向东依次为：西河园、锦江园、金沙园、望龙园、缤纷园、桥梁园、华龙园、闽风园和闽水园。南园位于闽江南岸，全长2850米，占地27.4公顷。南园的原生林较多，人工雕琢的痕迹相对较少。她由一个主入口广场和8个景区组成：荷塘月色、江畔闲情、梦里田园、童趣天地、十里花径、花乡广场、洪塘古渡、日沐椰风。', '', '', '', '', '', '', '', '', '免费开放。1-3人脚踏车租赁1小时50元', 3);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1516, '芦际潭森林公园', '芦际潭森林公园位于距长乐城区3公里的五竹村，总面积约12000亩，以我市西北山区蟛蜞山为中心，包括复老山、狮头山、石龙山等山林。公园内最令人流连忘返的景点是密林深处一个高约12米的瀑布，西侧岩壁上有明万历年间的摩崖书刻“芦际潭”，东侧岩壁上的“霖雨苍生”则是后人为纪念明崇祯年间长乐知县夏允彝来此设坛祈雨而刻的。', '', '', '', '', '', '', '', '', '', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1519, '长江澳风车田', '得天独厚的风神厚爱着平潭，使其拥有极丰富的风能资源。位于北部的长江澳风力田，不仅提供了最洁净的能源，也是摄影爱好者们眼里的绝佳景致。错落有致的风车一座座的耸立在沙滩上，在空中挥动着细长的叶片尽情舞动着。远远望去，碧海荡漾、天蓝云白、配上缓缓转动的风车组成了一幅极美的画面。', '', '', '建议自驾前往。自驾路线：平潭县出发途经馥祥路、X162约30分到达。', '', '', '', '', '', '免费', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1521, '海洋梦幻儿童游乐园', '海洋梦幻儿童游乐园位于福建省福州市罗源县滨海新城海洋世界海上搏斗2层。包含众多的娱乐项目，是孩子游玩的天堂，安全措施有保障。包含的项目有：碰碰车，小磨盘，迷你飞车、小火车，小蜜蜂，旋转木马，海豚王子、淘气堡，钓鱼，挖掘机。', '', '', '自驾建议驾车前往。', '', '', '', '', '', '自驾建议驾车前往。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1523, '宫巷', '三坊七巷之一。在安民巷之南，东西两端分别一八一七北路和南后街相接。宫巷旧名仙居，以巷中有紫极宫得名，是当今福州保护得最完整的古巷坊。巷内现存明代的建筑6幢，清代的建筑13幢。其中面积在千平方米以上的深宅大院有10幢之多。福州历代尤以近代名人多居于巷内，如清两江总督、福建船政大臣沈葆桢，林则徐之子林聪彝，清海军总长刘冠雄等。宫巷里的豪门住宅结构精巧，单是室内的木雕石刻构件就今人叹为观止。如漏花窗户采用缕空精雕，榫接而成，而且通过木格骨骼的各种精心编排构成了丰富的图案装饰。在木穿斗、插斗、童柱、月梁等部件上常饰以重点雕刻。各种精巧生动的石刻在柱础、台阶、门框、花座、柱杆上随处可见。可算是福州古建筑艺术集大成者。', '', '四季皆宜', '公交：福州市区乘159路、186路、173路、54路、16路等公交车在“乌山路口”站下车，步行8分钟可达', '', '', '', '', '', '无需门票。', 4);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1525, '百祭沟', ' 方圆五平方公里洞瀑联缀的百祭沟景区，位于福州市永泰县城西四十一公里处，与花岗岩鼓石堆叠如陈、洞径犬牙交错的玉石岭景区隔溪相望。若说百祭沟，民间早有传说：世人如为柴米油盐谋续，或为单身寻偶，或为商场问利，或为仕途求，只要站在百祭沟口面对大山虔诚祈求，准能迷中呈祥。而如今百祭沟有邑人修建栈道石径，直可通幽，游人怎奈裹足山口，身临深沟，在古木参天、乱藤飞涧的仙境中徜徉，珍珠瀑布滚珠流莹，双虹瀑布泛发七彩，人参瀑布体腴须开展，双狮瀑布奔腾咆哮，白龙瀑布...展开全部方圆五平方公里洞瀑联缀的百祭沟景区，位于福州市永泰县城西四十一公里处，与花岗岩鼓石堆叠如陈、洞径犬牙交错的玉石岭景区隔溪相望。若说百祭沟，民间早有传说：世人如为柴米油盐谋续，或为单身寻偶，或为商场问利，或为仕途求，只要站在百祭沟口面对大山虔诚祈求，准能迷中呈祥。而如今百祭沟有邑人修建栈道石径，直可通幽，游人怎奈裹足山口，身临深沟，在古木参天、乱藤飞涧的仙境中徜徉，珍珠瀑布滚珠流莹，双虹瀑布泛发七彩，人参瀑布体腴须开展，双狮瀑布奔腾咆哮，白龙瀑布腾空飞泻，三叠瀑布参差摇曳，使人大胞眼福。然这些美景它处不乏，何之炫耀!至于灵鼠望月、河马戏水、群象游弋、天蓬守望、金豹跃涧、雄狮饮瀑、巨蜥觅食、蛇王吞雾、鲤鱼潜潭，亦皆维妙维肖，众人啧啧称其乃大自然的造化、鬼斧神工，但亦不是此处独有，不足挂齿。且看，丹霞地貌中藏有数百米深的巨大环形天坑，且近临繁华都市却干百年来无人踏及，新奇不新奇？金豹峡百米岩壁如来佛祖五指分明，巳从西天取经凯旋的孙猴子还在那里面壁膜拜，奇怪不奇怪？天坑底有数百平方米的巨洞，洞中千只鸽子般大小的蝙蝠扑腾飞窜，稀奇不稀奇？雷霆万钧的双虹瀑布跃动的水光，竟能映动千年心状巨石，使其日夜不停地律动，罕见不罕见？天坑内壁上的“仙人洞”，十余丈高的洞顶中央“澡井”居然真有“天人”大书“仙人”二字，字迹丰妍圆润，玄妙不玄妙？人类生命之根与生命之门塑型在千米绝壁上，一左一右，玄秘不玄秘？收起', '百漈沟门票成人票¥40¥35.8起查看报价百漈沟门票双人票¥90¥71.5起查看报价百祭沟门票优待票（儿童/老人）¥20¥20起查看报价百漈沟门票团队票¥40¥33.9起查看报价百漈沟门票+彩虹滑道¥78¥64.9起查看报价百漈沟门票+高空滑索¥90¥84.9起查看报价百漈沟门票+彩虹滑道+高空滑索¥125¥114.9起查看报价百漈沟电子导游¥7¥3起查看报价暑期特惠 永泰百漈沟+嵩口一日游|含大门票+往返车+导游¥208¥158起查看报价', '', '', '', '', '', '', '', '', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1527, '青芝山', '青芝山在闽江下游连江琯头镇西北侧，青芝山距福州约42公里。相传古时山上有青紫色灵芝生长，故名青芝山；因青芝山上有很多岩洞，青芝山又称百洞山。青芝山是第二批省级风景名胜区。全青芝山由奇峰、异洞、怪石、流泉等组成108景，主要有五峰、十洞、十六岩、三十五石、一湖、三泉、三台、三室等，此外青芝山还有青芝寺、梅花楼、啸余庐、林森藏骨塔等建筑依山而筑。青芝寺，原建在八仙岩北。明万历年间，工部侍郎董应举退隐归田，开拓青芝名胜，移寺于此。清光绪十三年（1887年），重修大士殿。1934年辛亥元老、国民政府主席林森集资修建前殿（又称下大士殿）。寺宇小巧玲珑，别具一格，近年重新修饰，焕然一新。寺旁有狮子岩、三鱼石、五曲洞、蝙蝠洞、猿公岩、“石鲸”、“仙桃”、“仙掌”、“日轮”、“石鼎”、一线天、阆风台等景致。 　　梅花楼，在青芝寺左侧。原建于明天启年间(1621～1627年)，在山下梅屿村。当年董应举不阿附宦官魏忠贤，被弹劾落职，回家乡修此读书处，以寒梅傲雪，自喻风骨。1919年乡人重修青芝风景，移董家旧楼于寺左，并环植梅树于楼外。', '', '', '', '', '', '', '', '', '15元', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1529, '青龙瀑布', '永泰县青云山风景区中最奇的是“青龙瀑布”。青龙崖高80米，“一崖高耸接云天”。崖顶有一个呈半圆凹槽的断壁。水流从断壁的槽口跌落而下，被半空的一片岩石挡住。水石相击，铿铿锵锵；然后又再泻而下。如此经过类似的5个回合，形成美丽的“五叠泉”。“青龙瀑布”经几个场次的飞珠溅玉之后，集中水力又以雷霆万钧之势，凌空直下“青龙潭”。这个“青龙潭”也是古代火山的喷发口之一，今天却溪水盈盈，有竹排让人游戈，颇有一番诗情画意。', '青龙瀑布成人票¥35¥33起查看报价', '', '', '', '', '', '', '', '35元', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1531, '宋庆龄旧居', '宋庆龄旧居也是“保卫中国大同盟”中央委员会旧址，位于渝中区两路口新村5号（原3号）。旧居占地898平方米，建筑总面积665平方米，由主楼、后楼、防空洞等建筑设施构成，砖木结构。室内展出的实物和历史照片等共110件，反映了1942—1945年宋庆龄在渝期间，重新组建保卫中国同盟委员会并担任该会主席，致力于国共合作，争取国际援助，输送物资救助伤员、孤儿和难民，全身心投入抗日战争的历史事迹和她对中华民族的杰出贡献。', '', '', '乘坐1号线或乘109、114、118、124、128、138、152、268、299、402、403、411、412路等公交车，或乘皇冠大扶梯在两路口站下车后步行可达。', '', '', '', '', '', '免费开放', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1534, '钓鱼城Fishing town', '钓鱼城古战场遗址位于合川市城东5公里嘉陵江畔，占地2.5平方公里。  关于钓鱼城有一段传奇的历史：元1259年7月，蒙古军队分兵三路伐宋，大汗蒙哥在钓鱼城军民的顽强抗击下，被炮火击成重伤，后病逝于北温泉温泉寺。蒙哥死后，蒙军内部发生争夺王位的战争，削弱了进攻南宋和向欧洲扩张的力量，延续了南宋的统治，改变了世界的格局，因此钓鱼城被中外历史学家誉为“上帝折鞭之处”。  钓鱼城古战场遗址至今保存完好。主要景观有城门、城墙、皇宫、武道衙门、步军营、水军码头等遗址，有钓鱼台、护国寺、悬佛寺、千佛石窟、皇洞、天泉洞、飞檐洞等名胜古迹，还有元、明、清三代遗留的大量诗赋辞章、浮雕碑刻。1982年，钓鱼城被列为风景名胜区。', '钓鱼城成人票¥60¥57起查看报价钓鱼城优惠人群票¥30¥28.5起查看报价钓鱼城门票成人票¥90¥85.5起查看报价合川游船成人票¥30¥28起查看报价【商圈免费接】重庆合川钓鱼城+涞滩古镇+二佛寺纯玩一日游¥258¥158起查看报价', '春秋两季。', '汽车在【观音桥汽车站】乘坐到合川的汽车，抵达合川后在【塔尔门汽车站】换乘班车直达景点，车票3元/人。', '', '', '', '', 'http://www.hcdyc.com', '门票60元重庆合川区居民可凭本人身份证办理钓鱼城门票年票，每人每年100元;身高1.20米（含1.20米）以下的儿童、70周岁以上的老人、现役军人、烈属、革命伤残军人、 军队离退休干部、残疾人等可免门票游览钓鱼城;凭学生证可享受半价优惠。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1536, '华岩寺', ' 华岩寺位于九龙坡区华岩乡大老山，因寺南侧有一华岩洞而得名。该寺始建年代无史可考。清康熙及道光、同治年间，均相继扩建。华岩寺岩高百丈，形状像笏，寺内外松竹修茂，十分幽邃，有天池夜月、万岭松涛等八景；华岩寺分大寺、小寺。大寺殿堂建筑系传统庭园式砖木结构建筑群；分为前、中、后三殿堂，即大雄宝殿、圣可祖师堂和观音堂；寺左侧为接引殿；大雄宝殿内的十六尊者木浮雕，为寺院所少见，寺内还珍藏有印度玉佛及铜、玉、石、木、泥雕像及大金塔模型等；小寺即华岩洞，与大寺隔湖相望，为华岩寺之祖庙。 ', '', '', '在石桥铺乘坐到中梁山的222路车，在杨家坪乘坐到沙坪坝的204路，在袁家岗乘坐到中梁山的203、263路均可到达华岩寺。', '', '', '', '', '', '免费开放。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1538, '巫峡Wuxia Gorge', '巫峡位于重庆巫山县和湖北巴东县之间，西起巫山大宁河口，东至官渡口，绵延45公里，是三峡中最长最大的峡谷，俗称大峡，它峡长谷深，奇峰突兀，峡谷幽深秀丽，素以“秀”著称。巫峡是三峡中最连贯、最整齐的峡谷，分为东西两段，西段由金盔银甲峡、箭穿峡组成，东段由铁棺峡、门扇峡组成。整个峡谷奇峰突兀，怪石嶙峋，仿佛一条迂回曲折的画廊。著名的巫山十二峰分别坐落于两岸，南北各六。十二峰绮丽如画，姿态万千，是巫峡的精华所在。十二峰中尤以神女峰最为著名，峰上那一挺秀的石柱恰似亭亭玉立的少女。她每天最早迎来朝霞，又最后送走晚霞，故又称“望霞峰”。但是坐船游览巫峡的话，只能望见九座，江南的三座（上升峰、起云峰、净坛峰）必须弃舟登岸，在青石镇附近才可以看到。巫峡谷深狭长，日照时间短，峡谷中的水蒸气蒸腾不散，容易形成云雾。云雾千姿万态，似游龙、玉带、瀑布、轻纱，在阳光的照耀下，曼妙无比，形成著名的巫峡佛光。唐朝诗人元稹留下“曾经沧海难为水，除却巫山不是云”的千古绝唱，正是对巫山云雾的精彩描述。巫峡名胜古迹众多，除了著名的巫山十二峰外，还有陆游古洞、大禹授书台、神女庙遗址、孔明石碑等，无不以深厚的历史底蕴吸引着各地游人。游玩巫峡最合适的方式就是从巫山县的长江渡船码头乘船顺流而下，一路观赏壮丽的巫山十二峰和飘渺的巫山云雨。', '【长江三峡】0购物豪华黄金邮轮4日游—三峡大坝+小三峡¥1349¥1349起查看报价【官方直营】长江三峡  美维游轮4天3晚豪华游¥1850¥1850起查看报价', '每年3月--5月、9月--11月。', '乘三峡游轮或在巫山县乘船，会在巫峡停靠。', '', '', '', '', '', '免费开放', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1541, '镇蓟县下营镇常州村', ' 蓟县下营镇常州村是天津市最北、最高的唯一一个塞外深山村，共有90户、265口人，现有耕地154亩，林地12000亩，其中用材林8000亩，经济林4000亩，其中，耕地11289亩，林地147571亩，吴园总面积达54338亩，有各类成幼果树209.37万株，生产干鲜果品635万公斤，创产值1096万元，并逐步建成了万亩燕山板栗、千亩优质早酥梨、千亩优质大桃、千亩玫瑰花等果品、花卉生产基地。在此基础上，镇政府大力发展养殖业，成立了两个服务组织（即：肉鸡养殖服务中、生猪改良服务中心）服务人员16名。并...展开全部蓟县下营镇常州村是天津市最北、最高的唯一一个塞外深山村，共有90户、265口人，现有耕地154亩，林地12000亩，其中用材林8000亩，经济林4000亩，其中，耕地11289亩，林地147571亩，吴园总面积达54338亩，有各类成幼果树209.37万株，生产干鲜果品635万公斤，创产值1096万元，并逐步建成了万亩燕山板栗、千亩优质早酥梨、千亩优质大桃、千亩玫瑰花等果品、花卉生产基地。在此基础上，镇政府大力发展养殖业，成立了两个服务组织（即：肉鸡养殖服务中、生猪改良服务中心）服务人员16名。并先后与北京正大、北京华都两个公司签定了肉鸡饲养合同。全镇从事肉鸡、生猪、布尔山羊、散养蛋鸡、珍禽等十几种养殖，年出栏畜禽83万头（只），从业人员达1500多人。收起', '', '', '', '', '', '', '', '', '免费', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1545, '走马古镇', '走马古镇地处巴渝腹地的走马镇西临璧山，距今有700多年的历史。曾被市政府命名为“民间文学之乡”和“历史文化名镇”。早在明代中叶走马便有驿站，自古以来便是商贾往返渝州、蜀都的必经之地。来往的行人把各种新鲜事也带到了走马古镇，不计其数的民间文化作品，故事也被创作出来，世代相传。如今，走马古镇尚存的古驿道遗址、古街区、铁匠铺、老茶馆、明清建筑古戏楼、孙家大院、慈云寺遗址等。', '', '', '', '', '', '', '', '', '无需门票。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1546, '演武大桥观景平台', '厦门演武大桥被认为是离海平面最近的大桥，演武大桥景观平台位于演武大桥演武路立交的外侧，由三座栈桥和一处景观平台构成，环抱于演武大桥演武路立交外侧，向海湾深处伸展着。站在各个位置，闭上双眼，甚至有飞翔于海面的感觉。观景平台全长824米，总面积超过一万平方米，相当于27个标准的篮球场。其中，观海面长度约455米，最宽处达33米。游览时，可从位于世茂海峡大厦后侧的西入口、演武路西侧通往海堤外道路的中入口，以及位于海洋新村后侧的东入口进入观景平台。几乎是一伸手，就能触摸到厦门湾这片碧海。', '', '', '', '', '', '', '', '', '免费开放。只允许行人进入', 4);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1548, '蚩尤九黎城', '彭水县乌江左岸的蚩尤九黎城，以苗族始祖蚩尤文化为主线，集中展示了苗族的传统文化和建筑，充满了浓郁的苗族风情。九黎城主要建筑有蚩尤大殿、九道门、九黎宫、蚩尤神府、九黎神柱、北斗七星景观亭、百苗文化长廊、游客服务中心、购物长廊、美食一条街等苗族特色建筑。众多的苗族非物质文化遗产传承人会在这里表演绝活，如上刀山、下火海，古老的冷兵器展览等。九黎城还将在每年四月初八举办“祭祀蚩尤大典”，九月初一至初九举办“九皇斋文化节”。', '蚩尤九黎城成人票¥90¥72起查看报价『铂金纯玩』武隆仙女山+天坑地缝2日游（商务舱+高档酒店）¥580¥480起查看报价', '', '公共交通:彭水火车站坐公交车到城北隧道口下车（4元钱），走路到边河坝（大概10分钟内），坐边河坝到靛水的公交车可以直达蚩尤九黎城（3元钱）自驾:从重庆主城上渝湘高速，在彭水县西下道朝靛水方向行5公里左右，就可以抵达蚩尤九黎城。', '', '', '', '', 'http://www.ps9l.com', '90元/人', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1549, '大同路', '大同路始建于1929年，长1100米，宽9米，骑楼宽2.4米，是厦门第一波城市建设时出现的道路，也是完全由华侨出资购地，独立建房而形成的商品街。从1929年到1958年间，大同路是厦门最繁华的商业街，也是商品种类最齐全的贸易区。由于它靠近轮渡码头，地理位置上佳，所以很快成为货运量和人流量最集中的地方，商品流通量巨大。加上它毗邻邮局、海关等重要机构，形成贸易集市，甚至成为当时厦门的商业中心。', '', '', '公交乘坐4路、19a路、19b路、25路、30a路、30b路、97路、107路、112路、118路、127路、656路、841路、853路、941路、946路、952路到【轮渡邮局】站，下车步行16米即到。', '', '', '', '', '', '免费', 4);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1551, '重庆大轰炸惨案遗址', '抗日战争时期，1937年至1938年上海、南京、武汉相继被侵华日军占领，"国民政府"沿长江节节败退至重庆。此后，重庆不仅成为"国民政府"的陪都及政治、军事、文化中心，而且成为日军实施"以炸迫降"战略企图最主要的空袭目标。1987年7月6日，为纪念"七、七"事变50周年，"日本侵略者轰炸重庆纪事碑"落成仪式在此举行，并将该处列为重庆市文物保护单位。', '', '', '轻轨乘坐轻轨2号线在【较场口】站下。公交乘坐124、302、306、308、313、315路等公交车，至【较场口】下车，即到。', '', '', '', '', '', '免费开放', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1552, '日光岩寺', '日光岩寺位于鼓浪屿晃岩路60—62号，始建于万历丙戌，即万历十四年，1999年9月，完成总体规划改造。日光岩寺1998年登记为宗教活动场所，登记证号为厦宗02104，负责人谢黎华。 ', '', '', '', '', '', '', '', '', '无需门票。包含在鼓浪屿门票内', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1553, '天津节水科技馆', '天津节水科技馆位于天津市西青经济开发区大寺工业园内，展区布展面积1680平方米，是我国规模最大、展示内容最丰富、科技水平最高，集宣传和教育为一体的现代化节水科技馆。科技馆展区主要由序厅和四个相互关联的展厅（“大自然的恩惠”、“日益珍贵的水资源”、“开源节流的时代”、“共创人与自然和谐的明天”）组成。科技馆共设置展项200余组，通过融科学性、知识性、趣味性于一体的展览，突出“自然、人、水、科技”主题。此外，各展区设计力图在高科技的基础上运用互动展示手段，为参观者提供身临其境的体验之旅。', '', '', '', '', '', '', '', '', '免费开放。', 4);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1554, '安居古镇', '安居古镇是重庆市批准的20个历史文化名镇之一，于2008年授予第四批中国历史文化名镇。因其水陆交通方便，这里历来帆樯蚁聚，商贾云集，贸易繁荣，而且是兵家取道涪江的要塞和屯兵扎寨的住所。安居镇依山傍水，风光绮丽，景色迷人。历史上的“安居八景”和“九宫十八庙”曾闻名遐尔，前来游山玩水的文人骚客和烧香朝圣的信徒络绎不绝，摩肩接踵。  安居景色清幽奇特，风光旖旎，既有别具特色错落有致的古镇建筑（传统街区），又有遍布古镇精湛的历史遗迹（九宫十八庙，历代名人手迹）；既有风光绮丽的山水美景（特色生态农业），又有独具韵味的传统风情（龙舟竞渡，民间彩扎，名特小吃）。安居的“九宫十八庙”，现万寿宫、下紫云宫、元天宫、城隍庙、东岳庙、妈祖庙等八处保存较好。这些古建筑风格各异，造型独特，万寿宫具有东南亚异国风味，妈祖庙体现出安徽异地风情民俗，元天宫等处处可见龙形图雕，生动再现了博大精深的中华龙文化，显示了高超的建筑工艺水平。', '', '', '先从菜园坝、沙坪坝、陈家坪客运站乘车至铜梁县，到了铜梁县再转车至安居镇即可。', '', '', '', '', '', '无需门票。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1555, '《闽南神韵》演出', '结合了南音、南少林、戏剧、惠安女、郑成功、木偶等最具特色的闽南元素，融合了海洋文化和闽南文化的精华。整出戏分为六场，分别是水做的闽南、东南乐土、大海精神、宗教圣地、和谐家园和英雄故乡。通过精彩的演出让不了解闽南的人瞬间得到一个难忘的印象。', '【促销】《闽南神韵》普通区成人票¥220¥100起查看报价【促销】《闽南神韵》贵宾区成人票¥135¥124起查看报价《闽南神韵》贵宾区成人票¥100¥100起查看报价《闽南神韵》A区儿童/老人票¥198¥135起查看报价《闽南神韵》普通区成人票¥220¥74.9起查看报价《闽南神韵》B区儿童/老人票¥156¥100起查看报价', '', '公交乘坐8路、9路、86路、88路、311路、941路至【文化艺术中心】下，步行到达。', '每晚8点上演，表演时间1小时15分钟左右。', '', '', '', '', '180元', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1556, '北塘出海打渔船', '乘渔船出海、体验渔民生活，亲身体验捕鱼乐趣！', '', '', '', '', '', '', '', '', '不接受散客，只包船出海，20人小船1800元，30人船2600元，人数越多船型费用各不同，详情可电话咨询。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1557, '溥仪展馆', '为了最大限度地挖掘、保护、利用静园的历史人文价值，整理公司复原和仿制了部分室内家具及生活用品，并与吉林省社会科学院开展了课题研究工作，对上千幅历史图片和120万字的文史资料进行分析整理，完成了“末代皇帝溥仪在天津”展览资料的编撰工作，建成了爱新觉罗.溥仪展览馆。溥仪是中国历代帝王中唯一留下了大量生平照片的人，爱新觉罗溥仪展览馆以“紫禁城的黄昏”、“津门寓公”、“出关以后”为主题，分三部分展出了有关溥仪的百余张珍贵图片。 ', '', '四季皆宜', '公交乘坐公交3路、35路、50路、600路、606路、631路、632路、641路在【鞍山道】站下车即可。地铁乘坐地铁1号线在【鞍山道】站下即可。', '', '', '', '', 'http://www.tjjingyuan.com', '票价成人票价20元半价票10元优惠政策1.全年享受免票：身高1.2米以下儿童；70岁以上老年人凭本人老年证或身份证；现役军人（含武警）凭本人士官证、军官证。2.全年享受优惠票：60岁以上老人凭本人老年证或身份证享受半价优惠；中小学在校生凭本人学生证享受半价优惠。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1559, '曹禺故居', '曹禺故居位于天津市河北区民主道东侧。坐东朝西，砖木结构二层小楼。东至民主道21号院墙，西抵民主道27号院墙，北起南临民主道23院墙。现有小院狭窄，二层小楼，五间平房。23号院(前院)坐南朝北，环境同上，有过道二层，前后两个独立小楼。馆内拥有大量历史资料和近3000张馆藏照片。其中展出的照片有500余张，恢复性摆陈家具50余件，物件40余件。馆前铸有由著名雕塑家刘鑫创作的曹禺先生半身铜像，像高1.2米。', '', '', '', '', '', '', '', '', '免费开放。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1562, '天津外国语大学Tianjin Foreign Studies University', '天津外国语大学坐落在北方经济中心和港口城市天津，是一所多语种、多学科、多层次、开放型的高等院校，也是全国八所独立设置的高等外语院校之一。', '', '', '', '', '', '', '', 'http://www.tjfsu.edu.cn', '', 4);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1564, '牡蛎滩', '牡蛎滩是古海洋遗迹，由牡蛎壳堆积而成的，大约在距今5800－2200年前形成。目前牡蛎滩与七里海和贝壳堤一起组成了天津古海岸与湿地自然保护区，现已被批准为自然保护区。牡蛎滩的规模、剖面层厚、清晰度和壳体长度堪称世界奇观。牡蛎生长在潮间带下部和潮下带水面下，故将牡蛎层的顶面作为判定当时海平面的标志之一。', '', '', '', '', '', '', '', '', '免费开放。', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1569, '永宁城隍庙', '永宁城隍庙以中轴线为基准，由门楼、前殿、戏台、拜亭、主殿、和左右厢房组成。主殿面阔五间，进深三间。殿前有檐楼，抬梁式木结构。前后殿均为重檐歇山式屋顶，余为单檐歇山顶。建筑艺术结构严整、精美。其书法、联文、木雕、石雕、砖刻均出自明、清高手。门楼两侧的“雷厉”“风行”四个石刻大字，书法遒劲、端庄大气，令人赞叹不已。拜亭两侧的清代石雕狮子栩栩如生，雕工精湛，堪称石雕精品。庙内尚保存道光重修碑记、碑序各一方，对研究清代闽台关系史具有重要的参考价值。', '', '', '', '', '', '', '', '', '', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1571, '岵山', ' 岵山位于永春县城南部，是座拥有千年历史的典型闽南特色古镇。有茂霞、铺上、铺下、塘溪四个中国传统村落，有中国最有魅力休闲乡村，有乡村生态休闲庄园。整个镇古厝、古寨、古寺、古街、古树与稻田相互交错，山水相依，勾勒出一幅人与自然和谐的古镇田园山水画卷，是都市人释放压力、寻找“乡愁”的最佳选择。森林覆盖率达98%，风景优美，历史厚重。明朝石砌古寨，绵延成片的荔枝园林，充满人文气息的闽南骑楼式古街和异彩纷呈的古民俗。古厝、古寨、古树、古街、古民俗汇聚传统村落...展开全部岵山位于永春县城南部，是座拥有千年历史的典型闽南特色古镇。有茂霞、铺上、铺下、塘溪四个中国传统村落，有中国最有魅力休闲乡村，有乡村生态休闲庄园。整个镇古厝、古寨、古寺、古街、古树与稻田相互交错，山水相依，勾勒出一幅人与自然和谐的古镇田园山水画卷，是都市人释放压力、寻找“乡愁”的最佳选择。森林覆盖率达98%，风景优美，历史厚重。明朝石砌古寨，绵延成片的荔枝园林，充满人文气息的闽南骑楼式古街和异彩纷呈的古民俗。古厝、古寨、古树、古街、古民俗汇聚传统村落，随便走进一座古厝，都可见厝内精美的古木雕、砖雕、石雕、泥雕，形态各异，栩栩如生。古镇至今仍保留着闽南非物质文化遗产掌中木偶、南音等，走进古镇，不时可以在古厝或者古荔枝树下邂逅那已回荡千年的悠悠古乐。收起', '', '9月-11月是泉州最佳的旅游时间。 这里冬短而无严寒，温暖湿润，四季如春。', '建议自驾前往', '', '', '', '', '', '免费', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1573, '得月楼', '位于福建省金门县金城镇的水头村，由于滨海之故，一直饱受盗匪的侵扰，为捍卫家园，远赴他乡经商有成的华侨们，纷纷斥资构筑拥有防御自卫能力的枪楼。其中以旅居印尼华侨黄延煌于二十世纪三十年代兴建的「得月楼」最为特出。拥有西洋风味的得月楼，楼高为6公尺、分为2层楼，铁铸栅栏、花格窗棂、造型典雅的洋楼，却拥有4面突出的炮口，每个正面都有2个大炮口和6个小炮口，货真价实的保卫功能，让洋楼在华丽的外表下，更有一股凛然不可欺的高贵气势。', '', '', '', '', '', '', '', '', '免费', 4);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1574, '箭扣长城', '箭扣长城是北京有名的一处野长城。这段长城背靠陡峭山势，险峻秀美，而且未经人工整修，显得古朴自然，由于其原始和陡峭，出于保护文物与安全角度，未经有关部门允许，游客不得擅自攀爬。箭扣长城大致从北边的九眼楼开始，一直到南侧的牛犄角边为结束，全长约有20多公里，长城基本可以分为三段。九眼楼到北京结为北段，北京结到箭扣（又名涧口）为中段，箭扣到牛犄角边为南段。', '箭扣长城手机电子导游讲解¥28¥7起查看报价', '9-10月最佳，天气秋高气爽最为适宜。', '公交自北京城区东直门长途汽车站乘916路车，到怀柔杨家园下车，过马路右手方向有一个路口。左转直行180米到于家园车站乘公交大巴到西栅子村。怀柔——西栅子  发车时间是（上午11：30  下午16：30   ） 西栅子——怀柔（上午6：30   下午13：00）自驾自北京城区三环路上的三元桥出发，走京密公路（京承高速在怀柔出口下直行）。到开放路环岛。第三出口丰宁方向直行至第四个红绿灯左转。（神堂峪方向），沿着公路直行，遇见去神堂峪岔路口（神堂裕向右转）直行，沿着盘山路上山，到八道河大桥，桥头有西栅子生态观光园广告牌。左转进此路。直行即到箭扣售票处。温馨提示：在怀柔注意加油，进山后没有加油站。', '注意事项1、天梯、鹰飞倒仰、九眼楼、北京结一段路最险，从东向西走稍好一点，从西向东走更艰难。2、遇见倒塌的路段要绕出长城，在城墙的石缝里插上树枝做梯子，再爬墙进去；3、天梯是一段70度陡坡、70-80米长的长城，最窄处60公分宽，台阶40-50公分高、15公分台面，要四肢并用地爬；4、鹰飞倒仰有一处长城直立倒塌，要南辕北辙地绕道迂回过去，下山70-80度陡坡，要不抓住树枝，很难站住；5、北京结到望京楼一段在长城里，东侧是城墙，西侧是灌木，没有台阶，非常难走。', '', '', '', '', '免费开放。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1575, '领show天地', '领SHOW天地位于泉州市丰泽区行政功能区，它的前身为具有数十年历史、由108幢旧厂房构成的成洲老工业区，后来经过政府的重修改造，加入很多西式的建筑元素在内，成为一个独具风格的商业区域。有人说，每个城市都该有一条咖啡酒吧街，让本地人和游客有了放松休闲的去处！印象中北京有三里屯，上海有衡山路，而泉州可能就要数领show天地咖啡酒吧街了，它也是泉州唯一的酒吧街。即使你不想喝酒，白天来此，漂亮的建筑物也会让你有合影的念头。而且作为商业区域，这里也有挺多商铺、餐厅、健身美容等会所，完全可以让你很悠闲的打发一下午的时光。', '', '', '公交：乘坐202路、210路至【客运中心站】，沿泉秀街向西北步行即可到。', '', '', '', '', 'http://www.1showing.com/', '无需门票。', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1578, '灵应风景旅游区', '灵应风景旅游区位于南安市东北部洪梅镇玳瑁山，自省道305线沿洪（濑）六（都）公路可直达景区，北连南安乐峰、洛江区罗溪镇，南接洪濑镇，东与洛江区双髻山风景区遥相呼应。灵应风景旅游区地处南安东北部洪梅镇境内的玳瑁山山腰，风景区内有始建于五代后唐的灵应寺、观音广场、观音阁以及其他旅游配套设施。', '', '', '', '', '', '', '', '', '10元', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1580, '北京喇叭沟原始森林公园Labagoumen National Forest Park', '喇叭沟原始森林公园有首都后花园之称，又有“天然大氧吧”之美誉。这里有原始次生林7万亩，植物极其茂盛，海拔1700多米高的南猴岭是怀柔区的最高峰。春天漫山遍野的杜鹃花盛放，秋天红叶遍布，景色优美。整个景区由南猴岭、百丈崖、冰川三个主要观赏区组成，总面积为45平方公里，森林面积在90％以上，植物种类有677种，动物300多种，绿色旅游步道达60公里。这里有充满了神秘色彩的原生原始森林，有北京地区罕见的云海，有保留了大量历史遗迹的凤凰台，有北京地区绝无仅有的暑期冰川，有在西伯利亚才可看到的千亩白桦林，有壁立千仞的百丈崖，有令开车族有惊无险的十八盘道，有流传着神奇传说的壹善松，有具有奇功异效的长寿泉、爱泉、珍珠泉，原始自然风光一览无余。', '北京喇叭沟生态景区成人票¥40¥36起查看报价北京喇叭沟原始森林公园手绘导览图中文语音讲解¥10¥3.2起查看报价【喇叭沟门】赏红叶，白桦林中漫步，1日休闲游！¥180¥120起查看报价', '春可看满山杜鹃，夏可享受高海拔森林的清凉气候，秋可观色彩斑斓的秋叶。', '公交路线：1.东直门乘坐936于家园下车。（运营时间：往九谷口方向：6：50-19：00，往东直门外方向：4：50-17：00）乘坐h11路， 在汤河口站下车（换乘起点站首末车时间：06：00-18：00，终点站首末车时间：05：40-17：20）乘坐乘坐h15路（或h16路， h15路区间，936支，936支，936支），在喇叭沟门站下车。2.六里桥乘坐前往河北丰宁的长途汽车，告诉司机在喇叭沟门下车。（票价50/人）自驾路线：可从三元桥下京顺路到怀柔后，沿怀丰公路北行至喇叭沟门村北，有路标。', '一、请您购票后有序进入园区，沿设置好的旅游路线观光游玩，严禁爬野山，以免造成危险；二、严禁带火种进入林区，严禁一切野外用火；三、请您爱护环境，保护环境卫生，不得随地乱丢垃圾；四、园区内禁止砍伐林木、攀折花木、砍扒树皮、开采土石、采药狩猎等一切不利于生态环境保护的行为；五、请您遵守园区的“入园”和”闭园”时间,及时返回,禁止夜间野外留宿,避免发生人身伤害事故;六、请您爱护景观和服务设施,不得随意刻画、搬动损坏，请保持景观的完整和服务设施的完整;七、山高路滑,雨、雪、雾、大风天气严禁登山；八、老、弱、病(心脏病\\高血压\\精神疾病等)、残、孕等游客在无家人或监护人的陪同下严禁登山；九、65岁以上的老年人在无家人或监护人的陪同下禁止登山.', '', '', '', '', '40元', 3);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1581, '中国雕艺城', '中国雕艺城地处国道324线福州至厦门段的中心位置，占地108亩，是我国为数不多的集雕刻艺术品开发、生产、展示、商贸、旅游于一体的大型专业市场。惠安古城外的一处景点，随处可见惟妙惟肖的石雕，菩萨、动物、人像，喜欢石雕的朋友可以很饱眼福。内设有石雕工艺精品、影雕加工区及配套的展示馆和多功能商务中心。', '', '', '', '', '', '', '', '', '无需门票。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1583, '石花洞', '石花洞风景区的洞内常年恒温13度，夏季时是京郊周末避暑出游的好地方。石花洞内的石花形式多样，数量繁多，洞内的开放部分安装了彩色灯光，和各种石笋石花一起构成了奇幻瑰丽的景观，观赏和摄影都十分合适。交通攻略石花洞现有房山43路公交车可以到达景区门口处。若是自驾前来则只能到石花洞外面1公里左右的南车营村停车场，再向前没有停车场，而且有时当地村民会封路，在此步行约15分钟左右到达景区门口，也可以乘坐当地村民私人运营的摆渡车，每人约20元（距离不远，若非有老人小孩等体力不足者没有必要乘坐）。游玩路线参考石花洞洞内共有七层，目前向游客开放的区域为一至四层，有16个厅堂，一共一百多处景观，进入后一层层步行游览即可，步行路线长度大约2.5公里，一般观赏2小时即可，喜欢摄影的游客则需要更久。另外，景区有导游服务可以帮助讲解石花的形象和传说故事，导游价格为5人以下团队每次100元，5人以上每增加1人加10元。一层：观看石花洞是从一层开始，一层有“雄狮迎客”、“鲤鱼溪水”、“蓬莱仙岛”等十几个石笋群构成的景观。其中比较特别的是位于洞口附近的十佛雕像，共有十处佛和菩萨像。另外，洞内深处的“瑶池石莲”处，有国内少见的月奶石景观，构成了乳白色的一朵朵莲花状，十分漂亮，可以重点观赏。二层：二层处游览路线为1352米，是四层内游览路线较长、规模较大的一层，这里比较开阔，景观也都比较壮丽。有“竹篱茅舍”、“石瀑布”、“龙宫帷幕”、“擎天鸳鸯柱”等景观，比较大气奇幻，在洞内摄影十分合适。三层和四层：三四层的规模较小，这里的石花比较特别，与一二层成熟的体积较大的景观不同，这里大多是微小的和正在生长的石笋石花等，这里有“龙女绣花台”、“仙人镜”、“花笋塔林”等景观可以一一观赏。观赏后从位于四层右侧的台阶处出洞即可。 石花洞景区外约1公里处的南车营村有多家农家乐和小宾馆，游玩之后可以来这里吃吃农家菜或住宿，一般标间约100元左右。', '石花洞国家地质公园成人票¥70¥65起查看报价石花洞国家地质公园双人票¥125¥120起查看报价', '适宜每年5-10月游玩，夏季最佳。', '', '1、目前石花洞已经开放了4层，全程徒步游览，大概需要2—2.5小时，穿一双轻便的鞋非常有必要（不建议穿皮鞋）。2、洞里的地面有些湿滑，有的地方光线会暗一些，游览时需注意脚下并照顾好同行的老人和儿童（需要提醒的是，石花洞进出口，不在一起，不能原路返回，而且会有登高爬低的路，身体欠佳者及老人、小孩等人群，要根据自己的实际情况量力而行）。3、石花洞内常年恒温13℃，夏季前来注意带一件外套以免着凉。4、入洞之前最好准备好饮用水，爬洞时容易口渴。5、进洞游览不建议带过多没用的东西，以轻便为主（婴儿车基本用不上，带上就是累赘，游览的时候也很不方便）。6、石花洞旅游自驾最方便。因游览时间不长，因此可安排为半日游或一日游，亦可根据个人兴趣与周边的潭柘寺、周口店猿人遗址或其他景点组合一日游览（无需住宿，用餐以景区内的慢生活农家院为佳）。7、因景观环境限制，洞内不设卫生间，请提前做好准备。', '', '', '', 'http://www.shihuadongcave.com/', '70元', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1584, '宿燕寺', '宿燕寺是在宋代海岸寺废墟上建造的，坐北朝南，前案峰峦连绵，后背山峦围护，地形似“燕子归巢”，故名。山巅有老子骑青牛景象。近几年经过改建扩建，建成天王殿、大雄宝殿、大悲殿及两廊，附有4个亭、莲花池及花园、功德堂赠药处等，景观一新。', '', '', '', '', '', '', '', '', '5元', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1586, '什刹海酒吧街', '什刹海酒吧街是北京最具文艺气息的酒吧聚集地，酒吧或在树影婆娑的什刹海边，或在老北京风情的胡同四合院内。相比工体、三里屯酒吧街等地，这里的酒吧更显安静，很多清吧、音乐吧里经常有驻唱歌手，唱得也很不错。沿湖酒吧什刹海附近沿湖的酒吧主要分布在三个部分，分别是荷花市场及前海部分、银淀桥附近，还有后海部分。消费的价格一般是前海较贵，银淀桥次之，后海较便宜。酒吧的规模也与价格成正比。酒吧大多是古式建筑，湖边环境十分优雅，有些酒吧还会沿着湖边的马路旁摆上桌椅，夏夜时在外饮酒聊天，非常惬意。不过由于近年来的火爆，酒吧泛滥，所以服务和种类也开始良莠不齐，在街上拼命拉客和低俗表演等扫兴情况也常会出现，需要选择适合自己的酒吧前往。其中，火木酒吧、甲丁坊、天与地等酒吧以乐队演出颇受欢迎，可以去试试。胡同酒吧相比沿湖酒吧的泛滥和喧闹，什刹海附近胡同里的酒吧则显得更有品质。在什刹海西侧的几条胡同里，都有很不错的特色酒吧。酒吧大多都在传统四合院内，虽然没有沿湖的景观，但是环境安静优雅，有各自的主题和特点。有些环境非常舒服，有些十分小资，有些会有很不错的驻唱乐队和歌手，近年来红起来的一批民谣音乐人中不少都曾在此唱过歌。如果不去酒吧，晚上只过来逛逛街也是不错的选择，夜晚时站在银淀桥上，两边的湖边树影婆娑，岸上灯红酒绿，构成一幅美丽的城市夜景，适合拍照摄影。', '夜游·北京旧鼓楼大街+广茗阁茶楼+烟袋斜街+什刹海酒吧街夜游¥150¥80起查看报价', '', '公交1、乘坐60路、82路、90内、90外、107路、124路、204夜班车内、204夜班车外在【地安门外】站下车。2、乘坐13路、42路、90内、90外、107路、111路、118路、204夜班车内、204夜班车外、09路、612路、623路、701路在【北海北门】站下车。3、乘坐5路、111路、124路、210路夜班车、609路在【地安门内】站地铁乘坐地铁6号线在【北海北门】站下车。', '', '', '', '', '', '无需门票。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1587, '广化寺', ' 千年古刹广化寺座落在市区凤凰山麓。凤凰山亦称南山、南湖。这里松柏苍翠，山泉鸣涧，清幽宁静。人行其间，绿蔓牵衣，别有一番禅意。相传千年之前，滔滔东海之水，直涌至南山脚下，行云流霞与青翠松柏交相辉映，构成了一幅无比生动的画卷。故有“南山松柏”佳景之称。广化寺始起于南朝陈永定二年（公元558年）。这里原是邑儒郑露三兄弟结庐讲学之所，郑露三兄弟世称“南湖三先生”。后来，有一位金仙禅师到此潜修佛道，郑氏三兄弟受禅师感化， 遂舍庐为庵，取名“金仙庵”，亦名“金...展开全部千年古刹广化寺座落在市区凤凰山麓。凤凰山亦称南山、南湖。这里松柏苍翠，山泉鸣涧，清幽宁静。人行其间，绿蔓牵衣，别有一番禅意。相传千年之前，滔滔东海之水，直涌至南山脚下，行云流霞与青翠松柏交相辉映，构成了一幅无比生动的画卷。故有“南山松柏”佳景之称。广化寺始起于南朝陈永定二年（公元558年）。这里原是邑儒郑露三兄弟结庐讲学之所，郑露三兄弟世称“南湖三先生”。后来，有一位金仙禅师到此潜修佛道，郑氏三兄弟受禅师感化， 遂舍庐为庵，取名“金仙庵”，亦名“金仙院”。这便是广化寺的前身。隋开皇九年（公元598年），来自天台山的无际禅师募资扩建为“金仙寺”。唐景云二年（公元711年），长老志彦应召入宫讲《四分律》，奏闻当年无际禅师持诵《法华经》，因受灵应石涌白泉的事，睿宗遂赐寺名“灵岩寺”，并由大书法家柳公权书匾，以此名重一时，成为八闽一大丛林。宋太平兴国元年（公元976年），始改名为“广化寺”。收起', '【错峰出游】五台山品质二日游¥359¥295起查看报价', '', '', '', '', '', '', '', '3元', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1589, '海丝电影小镇', '你想象中的民国是什么样的呢？风起云涌的十里洋场？风情万种的旗袍美女？复古骑楼旧海报？满街奔跑的叮叮车？想足不出户体验民国风韵？想穿越百年，体验那个时代的精致与典雅吗？一起来先睹为快吧！行走于海丝电影小镇，十几栋具有南洋风格的楼房拔地而起，淡雅的民国风扑面而来。乘坐马车，随着身着民国服饰的车夫一路奔驰，叮叮的声音彷佛回到了民国的感觉。', '', '', '', '', '', '', '', '', '', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1591, '石林峡Shilin Gorge', '石林峡景区以峡谷、悬崖、石林和瀑布等自然风光为主，山清水秀，环境优美。景区内还有旋转木马、激流勇进等娱乐项目，景区外则有度假村、农家乐等，是京郊周末度假娱乐的好去处，也是夏季的避暑胜地之一。游玩时间和路线参考石林峡景区较大，纵深约有5-6公里左右，主要以一座石林山和一条峡谷组成，石林山又分为前山、后山，游客可以从前山登上山顶，从后山下山，然后沿着峡谷的栈道返回景区大门。石林山的海拔约700多米，爬山需要3小时左右，全程步行游玩约需5-6个小时。不过前山和后山各有一条索道，单程每次50元，体力不够或时间不足者可以选择乘坐。精彩刺激的娱乐项目景区的入口位于东侧，进入后首先会来到一片娱乐项目的所在地，这里有激流勇进、峡谷漂流、高空飞翔、旋转木马等多项游乐项目，每项20-40元左右，有兴趣的可以体验一下。登山沿途风光优美然后便可以通过栈道从前山登山，山高有700多米，爬山大约需要3个小时，需要一定体力，如果体力不足的话也可以选择索道，可以把游客送到距离山顶还有几十米的地方。前山的风景以森林风光为主，还可以在山上俯瞰山脚下的石林湖，非常漂亮。登山的路上会经过松树坡、观湖亭、穆帅树等几个景点，可以观赏一下。到达山顶区域后，视野开阔，可以俯视山下的石林风光，陡峭秀丽，十分壮观。这里还有华夏第一鼓、古今第一钟、北国第一锣等景点，可以亲手敲钟打鼓，较有趣味。游玩山顶之后便可从后山下山，可以看到情人谷、灵芝亭等景点。到达山下后，便沿着峡谷中的栈道返回，一路上两侧都是高耸陡峭的崖壁，还有很多奇形怪状的巨石，非常的奇幻壮丽。河流、瀑布等遍布其间，也十分秀美。每到夏季时，这里更是凉爽宜人，是避暑的佳地。路上会经过九天飞瀑、一线天、银鱼潭等景点，可以一一参观。住宿餐饮 在景区的大门外，有景区官方的石林峡度假村，标间约200-300元每间，条件较好。另外，门口的雕窝村里还有很多农家乐，可以品尝到地道的农家菜肴，房间约100元每晚（周末会涨价几十元），可以选择入住。 景区内用餐不便，可以在门外的农家用餐，价格也还算实惠，山野菜、柴锅水库鱼、松蘑、柴鸡等特产都比较不错。每到十月时，当地还会有原生态的柿子成熟，可以品尝一下。', '石林峡成人票¥68¥58起查看报价石林峡门票+UFO玻璃观景台¥108¥98起查看报价石林峡门票+单程索道票（4选1）¥168¥153起查看报价石林峡门票+单程索道（4选1）+观景台¥208¥98起查看报价石林峡成人票+往返索道+观景台¥200¥98起查看报价北京石林峡手绘导览图中文语音讲解¥20¥3起查看报价挑战UFO|北京平谷京东石林峡纯玩一日游¥78¥68起查看报价【中秋特惠】石林峡赏景巴士一日游 （含采摘活动）¥188¥148起查看报价北京石林峡纯玩一日游 高空玻璃观景台¥109¥78起查看报价北京古北水镇+石林峡纯玩一日游¥88¥30起查看报价【包车游】北京石林峡+玻璃栈道包车一日游【周末游/团建拓展】¥998¥898起查看报价', '四季皆宜', '公交车路线：东直门乘852到平谷区医院，换乘区内25路公交车石林峡下车。自驾车路线1：京密路-顺义枯柳树环岛-平谷方向-平谷县城918总站外岔路口左转-老平蓟路-胡庄路口-黄松峪石林峡方向即可到达。自驾车路线2：机场高速-三号航站楼方向上京平高速（约40分钟）-密三路出口左转密云方向（约15分钟）-昌金路口右转金海湖蓟县方向（约25分钟）-黄松峪石林峡路口左转（约10分钟）到达。从胡庄往北行驶8公里到达石林峡景区。', '1、景区进入冬季防火期，2017年11月22日-2018年1月31日不对外开放，出行前建议提前致电景区或关注景区官网公告。2、景区门口附近有卖零食和饮用水的小店，但进入景区深处就买不到了，所以登山前要备足饮用水和零食。3、夏季登山时阳光较强，要准备遮阳帽、墨镜等装备。', '', '', '', 'http://www.jdslx.com/cn/index.html', '45元', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1594, '中国电影博物馆', '中国电影博物馆建于中国电影诞生100周年时的2005年，是全国唯一以电影为主题的国家级博物馆。除了在博物馆了解中国电影各个阶段的发展历史，这里同时也是一家电影院。很多经典的老电影和时下的最新电影都能在这里观看。电影博物馆展区最主要分为六个展区，分别为序厅、电影的发明、中国电影的诞生和早期发展、革命战争时期的中国电影、新中国电影的创建与发展、改革开放新时期的中国电影几大展厅，除此之外，还有美术电影、儿童电影、科学教育电影、译制电影、新闻纪录电影等多个电影种类的专题展出。进入后一一参观即可，草草参观一圈2小时左右。几个主要展厅内展示的便是一百余年来中国电影的发展脉络，展览系统详细，对于对电影感兴趣的游客来说是了解知识的绝妙之处。展馆内有古老的电影放映设备、道具，还有早期电影的布景复原等，展品非常丰富。其中比较珍贵的是郑正秋的书房、德国产手摇无声电影放映机、任庆泰70岁时拍摄的照片的金属底板等，很多展品都在中国电影发展的历史上具有标志性的地位，可以好好了解一下。除了观看展览外，博物馆同时也是一家电影院，拥有全球最大的胶片巨幅银幕。这里会播放时下在线的最新电影，一般票价会比外面常规电影院便宜一些。另外，每天这里还会有几场经典老电影播放，老电影为公益性质，票价约2元左右，感兴趣的朋友可以前去观看。', '', '四季皆宜', '公交1、乘坐公交851路、973路，【黑桥村西站】下车，按路牌指示方向，步行即可到达。 2、乘坐公交402路、418路、688路，【南皋站】下车，按路牌指示方向，步行即可到达。 出租车3公里以内13元，基本单价每公里2.3元。燃油附加费每运次1元。自驾东直门 — 机场高速（酒仙桥出口） — 酒仙桥路 — 环铁北桥 — 南皋路、南影路 — 中国电影博物馆 望京 — 大山子桥 — 酒仙桥路 — 环铁北桥 — 南皋路、南影路 — 中国电影博物馆', '', '', '', '', 'http://www.cnfm.org.cn/', '免费开放。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1595, '福建省美术馆', '福建省美术馆是一所公益性社会文化事业机构，直属福建省文化厅领导， 是具有征集、收藏、保管美术精品；举办美术作品宣传展览；开展有关学术研究、交流等工作的职能，在省内外具有一定影响的近现代美术博物馆。2003年7月，福建省美术馆新馆落成，坐落于美丽的西湖与左海交汇处，绿树成荫，碧水环绕，是现代建筑与美学的完美融合，是一座花园式的艺术殿堂。增配的先进的中央空调、恒温恒湿、消防保安系统及自动化管理设施都为该建筑的科技化含量和安全系数进行了全面的提升。现有可提供展览使用面积1200平方米。建馆30年至今办展近千场次，接待观众六百多万人次，展出作品五万多件。建馆三十年来，通过专题征集、接受捐赠、上级拨交、以画代租、专款收藏等多种途径，共收藏油画、国画、漆画、版画、农民画、书法等作品三千余幅，其中有徐悲鸿、陆俨少、林散之、亚明、李剑晨、陈子奋、宋省予、李硕卿、顾一尘、李耕、李霞、罗晓凡、乔十光、王和举、郑益坤、吴川、吴嘉诠等数百位画家的代表作及获奖作品。', '', '四季皆宜', '1、乘坐87路 , 在山海观站下车，步行990米至福建省美术馆  2、乘坐66路 , 在省杂技团站下车，步行 618米至福建省美术馆  3、乘坐109路 (或 121路 ) , 在西湖站下车， 步行789米至福建省美术馆', '', '', '', '', 'http://www.fjmsg.com/new_index.asp', '美术馆是免费开放的，馆内的部分展览会收费的。', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1596, '上海当代艺术博物馆', '上海当代艺术博物馆坐落于黄浦区黄浦江北岸，与黄浦江斜对岸的中华艺术宫遥遥相对。当代艺术博物馆主要展出各类当代艺术作品，经常会有不同的专题展览，你可以在这里欣赏到各种抽象艺术作品或者新概念艺术展品，充分激发你的审美观和想象力。博物馆的前身是始建于1897年的南市发电厂，其165米的钢筋混凝土烟囱笔直高耸，很远就能看到这栋工厂外形的建筑。博物馆内宽敞明亮，可见到粗犷的钢结构大梁，厂房改建的痕迹明显，也很有历史感。展厅主要位于博物馆的一至五层，五层还有咖啡厅，而七楼的云餐厅可提供正餐。博物馆的展品通常并不固定，经常会有不同的专题展览，比如曾举办过的艺术设计展、时代肖像展、瑞士景观设计、法国蓬皮杜中心藏品展、卡地亚时间艺术展等等，所以可能你隔一段时间再去时，看到的展品与上一次会完全不同。如果想具体理解艺术展品的含义，可在一楼租借语音导览器，一般为每台20元。博物馆的展览是免费开放的，不过也会常有一些特展需要购买门票才能入内，根据特展不同票价也不同，具体可先在博物馆官网上查询当前展览内容和门票情况。当代艺术博物馆也是上海双年展的主场馆，每两年一届的双年展是亚洲最具影响力的艺术展览盛会之一。', '', '', '公交市内乘坐1030路、1212路到【苗江路望达路】站下车即到。', '', '', '', '', 'http://powerstationofart.com/', '博物馆免费开放，举办展览期间另外收费，详情可咨询景区。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1600, '童乐园', '福州童乐园是福州市政府搭建的集科普教育、娱乐体验、产品发布、创意交流于一体的文化基地。其地处福州市鼓楼区通湖路衣锦坊67号，位于三坊七巷建筑群西北角，西靠通湖路、东接洗银营、北临雅道巷。', '', '', '公交乘坐54路、66路到【杨桥路口】站，下车向南步行120米即到。', '', '', '', '', '', '单次卡45元，15次卡300元。', 3);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1601, '玉渊潭公园Yuyuantan Park', '玉渊潭公园是北京市区最大的公园之一，也是市民们放松漫步的好去处。这里有东、西两座大湖，湖边杨柳依依，景色非常优美。公园里还有北京市区最大的樱花园，每年3、4月份玉渊潭赏樱，是整个京城的热门活动。玉渊潭公园在东南西北几个方向均有入口，园内面积较大，长约有2公里，宽将近1公里。公园内部有众多景点，可以概括为北侧樱花园、中心处东西两大湖和南侧游乐观景区几个部分。游客进入园区后步行游览，一般游玩小半天左右。漫步樱花树下北侧的樱花园是公园内最著名的景点，樱花园是一座园中园，也是北京市区最大的樱花园。园内种植了从日本引进的几千株樱花树。这些樱花品种不同，花期也有早有晚，可以接替开放，使得樱花园每年的3、4月份有一个多月的时间都能看到盛大的樱花，拍照观赏都十分合适。每到此时，园内也会举办樱花节，附近会搭起很多小吃摊和特色的购物小摊，游人如织，十分热闹。不过届时公园的票价会上涨到10元，周末、清明等节假日也会有人山人海的情况出现。泛舟东西湖中间两处大湖分别是东湖和西湖，两个湖泊的长度都在700米左右，周围有垂柳树木，远处的电视塔倒影湖中，环境非常优美。湖上有几处码头，可以划船游湖，船分为电动和脚踏两种，座位有4-6人不等，租船的参考价格在每小时60-120元不等，需要付200-400元左右的押金。除了乘船游湖外，在湖边散步也是不错的选择，每到傍晚时分夕阳西照，更是十分惬意。其它游玩项目湖南侧区域分为两部分，东边以观赏区为主，这里有留春园、景观大道等处可以游玩，有树木园艺、亭台楼阁等，类似园林风光。西边则有一些少儿娱乐项目，如碰碰车、花果山漂流、小游乐园等，每项收费一般在20元左右，适合带小朋友游玩的游客前来。而每年到冬天时，西边还会有一个雪上游乐园，可以体验滑雪圈、冰车、马拉雪橇等娱乐项目。', '玉渊潭公园6人电动游船1小时票¥150¥130起查看报价玉渊潭公园手机电子导游¥10¥1.3起查看报价', '每年3月下旬至4月中旬为园内赏樱的最佳时期，当你看到大街上榆叶梅开放的时候，那么赶快来园内樱花园。', '公交东门：东门位于三里河路东端，紧邻钓鱼台国宾馆（和钓鱼台大酒店）、西长安街，可乘坐320路、114路(无轨电车)、717、727路等， 【木樨地北站】下车，也可乘13、21、37、65到【三里河西口站】，也可坐一线地铁到【木樨地站】下车后北行。西门：与中央电视塔隔路相对，可乘坐300、323、374，以及特8，运通等多路抵达。南门：可乘坐1、4路等大量长安街的公交线路，或乘地铁在【军博站】下世纪坛北面入口。北门：位于阜成路航天桥东侧，海军总医院西侧路往南。可乘坐121、846、37 路【马神庙】或【白堆子站】下车。自驾由公主坟向北方向，要提前驶出主路，西门北侧有停车场。由北向南，要走航天桥下，可将车停在电视塔。', '1. 尽量避免周末或者重要节假日前往，届时园内游客数量会猛增，影响游玩质量。2. 植物园中没有什么像样的饮食，最好能够自带干粮。3. 温室需要单独买票，票价较高，根据部分游客反映，如果你不是对植物有特别的爱好，可以不入内，这样既节约了费用，又腾出了时间游玩其他的景点。', '', '', '', 'http://www.yytpark.com/', '门市价2起。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1606, '上海邮政博物馆', '上海邮政博物馆位于苏州河北岸，坐落于上海邮政总局大楼内。在这座历史悠久的老建筑中你会看到有趣的火车车厢邮局以及各种有趣的老邮筒、老信箱，还能了解到邮政业务的发展历史，尤其可让青少年们大开眼界。邮政总局大楼始建于1924年，一进大门，罗马式的立柱、旋转型楼梯、房顶上高挂着华丽的吊灯，满眼都是欧式建筑风格。整栋楼为“U”字型，底层的中庭陈列着大清邮政马车、1917年购置的第一辆邮运汽车、第一条定期航空邮路飞机以及“行动邮车”火车车厢邮局等实物模型，还能看到大清邮政局的场景。二楼是宽敞的邮政大厅，这里分为四个展厅，讲述了古今中外的邮政发展史，可以看到各种样貌奇特的老邮筒和信箱，珍宝馆里还展示了不少珍稀邮票，比如“黑便士”、“大龙邮票”以及“绿衣红娘”等邮票珍品。在这里小朋友们可以模拟写信、体验在古老的邮局里投递快件的感觉。大楼五层是屋顶花园，东南角有一座巴洛克特色风格的钟塔，站在这座观景平台上可观赏苏州河风光、远眺浦江两岸的美丽景色，很适合拍拍照。', '', '', '公交乘坐25路到【南崇明路】站下车。乘坐17、19、929路【河南北路天潼路】站下车。地铁乘坐10号线到【天潼路】站下车，2号口出。', '', '', '', '', '', '免费开放。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1607, '百里画廊', ' 景区位于延庆区东北部千家店镇，属延庆生态涵养区的核心区，总面积371平方公里，距县城40公里，距市区110公里。2007年被评为北京市自驾游10条最佳线路之首。景区因涉及滨河环线112华里，“百里山水画廊”因此得名，四季皆宜。沿途包括一环三区十二个空间节点，沿途有燕山天池、恐龙游乐园、硅化木、乌龙峡谷、滴水壶、龙王庙、关帝庙等景点。展开全部景区位于延庆区东北部千家店镇，属延庆生态涵养区的核心区，总面积371平方公里，距县城40公里，距市区110公里。2007年被评为北京市自驾游10条最佳线路之首。景区因涉及滨河环线112华里，“百里山水画廊”因此得名，四季皆宜。沿途包括一环三区十二个空间节点，沿途有燕山天池、恐龙游乐园、硅化木、乌龙峡谷、滴水壶、龙王庙、关帝庙等景点。春天百花盛放，夏日这里是名副其实的避暑胜地和绿色氧吧，到了秋日，更是一片金黄的景象，赏心悦目。 收起', '', '', '', '', '', '', '', '', '暂时停业，预计2019年4月开放；景区内小景点另外收费，详情可咨询景区。', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1609, '溪山温泉', '溪山温泉度假酒店-位于海峡文化村中心位置，背倚松竹苍翠之汤岭，面傍波光粼粼之鳌江，潺潺西溪环绕而过，优质温泉闻名遐迩，与国际标准18洞温泉高尔夫球场一路之隔，与西溪国家森林公园遥遥相望，堪称休闲旅游度假之首选。      溪山温泉资源得天独厚，不仅有深厚的历史底蕴，且水质佳，素有 “福建温泉之乡溪山第一” 之名。溪山温泉度假酒店的主题是“品味、休闲、养生”。温泉文化是“养生文化”，应该赋予每一位到来的客人一个静心的环境，在真正的自然山水，让客人置身真正的大自然中，放松心情。只有环境清雅了，心情安逸了，才有可能达到养生的效果。       溪山温泉拥有优质温泉水——日出水达7000吨的含氟、含氡温泉，中国首个高端会员制温泉俱乐部，独有的花样四季SPA等。3万平米亲水温泉公园，拥有绿荫环抱、种类繁多的露天汤池百余口，豪华汤屋五十余间，水上泡汤潘溪风帆十余艘，另有温泉SPA及理疗等养身项目，戏水、沙滩、游艇、三洞高尔夫球场等娱乐设施一应俱全，为消费者带来愉悦、健康的休闲时光。', '', '四季皆宜', '建议自驾', '', '', '', '', 'http://www.xishanresort.com/', '168元', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1611, '南海子郊野公园', '南海子公园是北京四大郊野公园之一，也是北京市最大的湿地公园，全部建成后总面积超过11平方公里。南海子公园位于大兴区东北部南五环南侧、大兴新城与亦庄新城之间，是北京落实城南行动计划的第一个重大生态工程，于2012年全部建成。南海子公园水面占五分之一，重点建设湿地景观、皇家文化、麋鹿保护、生态休闲等功能区，突出历史文化特色，再现昔日皇家苑囿风采，使之与北京北部奥林匹克森林公园和中心城区历史文化景观遥相呼应，成为北京南北中轴线上另一颗生态和文化明珠。', '', '四季皆宜，春秋最佳。', '公交乘坐 453路、开发区6路、兴16路、 兴59路，在【南海子公园站】下车', '', '', '', '', '', '免费开放。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1612, '探险岛', '探险岛让游客们置身于新发现的远古部落中，探索神秘世界，发现深藏的宝藏。巍峨的雷鸣山将为探险岛之旅指出明确的方向。或在“古迹探索营”走出自己的探险之路，或在“翱翔飞越地平线”里横跨世界和时空，抑或是坐上“雷鸣山漂流”的筏艇深入探险岛腹地，探索一只庞大神秘巨兽所在的黑暗领域。', '', '', '迪士尼园区内可步行前往', '', '', '', '', '', '包含在迪士尼度假区门票内，通用迪士尼度假区门票。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1616, '北岚岭', '平潭北岚岭近乎保留原生态，海滩没有被人为修整，水产品丰富，特别是那淳朴的民风简直似世外桃源。', '', '', '', '', '', '', '', '', '无需门票。', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1621, '上海当代艺术馆', '上海当代艺术馆（MOCA Shanghai）座落于市中心的人民公园内，与上海市政府、上海大剧院、上海美术馆及上海博物馆为邻。艺术馆共两楼，虽然面积不大，却定期举办不少展览，除了纯艺术的展览也有贴近大众的品牌展、设计展。艺术馆原址为人民公园内一个温室。在建馆过程中，保留了场馆的外部玻璃墙，场馆内部则进行大规模重建。场馆既保留其优越的自然照明条件以及公园的卓越景观，同时在增设多项设施后也满足了博物馆的专业要求。艺术馆总面积为4000平方米；展区为一楼与二楼展厅，总计面积近1800平方米。所有展览区域都配备专业的温控和湿控设施。艺术馆是由龚明光基金会出资建造，经上海市政府批准的非盈利且独立运作的艺术机构，致力推广中国及全世界的当代艺术。上海当代艺术馆由具丰富策展及行政经验的团队经营管理，旨在培养人们对中国及世界当代艺术与设计的欣赏能力，并不断推进上海与全世界之间的文化交流。', '', '', '乘坐1号线、2号线、8号线至人民广场站下。', '', '', '', '', 'http://www.mocashanghai.org/', '活动、展览时价格以实际展出票价为准，详情可电话咨询景区。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1624, 'AED(上海海洋水族馆)Shanghai Ocean Aquarium', '上海海洋水族馆包括中国展区以及美洲展区等等，每个展区的海洋生物种类都非常的丰富。整个展馆就是以主题、图片、等不同的效果，展示的各类的海洋生物。海洋馆里还有世界上唯一展示亚马逊热带雨林的观光隧道。在一层还包含了一个快餐厅。', '', '', '乘81、774、779、939、993、蔡陆专线、陆家嘴金融城1路、陆家嘴旅游环线等公交车在陆家嘴环路丰和路站下车。', '', '', '', '', 'http://www.sh-soa.com/', '门票:成人票160人民币/儿童票110人民币/老年人(60周岁以上)90人民币/现役军人、离休干部、残疾人70人民币 (1月1日-12月31日 周一-周日) tips: 身高1.0-1.4米的儿童，儿童不得单独入馆参观，需由成人陪同。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1626, '三平寺', '三平寺内供奉开山始祖杨义中高僧，被尊称为“三平祖师”，唐玄宗曾赐号“广济大师”。三平寺始建于公元866年的唐代，至今香火鼎盛，在闽南及港澳、东南亚地区久负盛名，香客络绎不绝，尤其是每年农历的正月初六、六月初六、十一月初六以及三平祖师的诞辰、出家、圆寂之日，更是人声鼎沸、香火缭绕。现存的寺院是在清末时重修的，整个寺依山而筑，有山门、大雄宝殿、祖殿、斋堂僧房、塔殿及钟、鼓二楼等建筑，结构严谨。近年又兴建了广济园、百果园、迎客园、仰圣山庄等配套设施。', '漳州平和三平寺直通车/闽南信念/拜祖师爷（静心朝拜之旅）¥198¥198起查看报价', '', '', '', '', '', '', '', '包含在三平景区内', 2);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1627, '明日世界', '“明日世界”充满了无尽可能并展现出未来最光明的愿景。迪士尼运用各类创新技术为这个园区带来全新面貌：新的星际探险项目“巴斯光年星际营救”带领游客们勇往直前、超越无限；“喷气背包飞行器”让人们突破重力束缚；“创极速光轮”包裹在巨大的半透明穹顶建筑下，幻化着不同光彩，吸引游客跨上两轮光轮摩托队列，挑战全球迪士尼乐园中最紧张刺激之一的过山车项目。游客们还能在“明日世界”旁体验到两个专为上海迪士尼乐园打造的互动体验——“星球大战远征基地”和“漫威英雄总部”。', '', '', '', '', '', '', '', '', '包含在迪士尼度假区门票内，通用迪士尼度假区门票（399元）', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1628, '大嵩岛', '神秘而美丽的大嵩岛又名大墩岛，由于是一座无人居住的孤岛，所以大嵩岛成为平潭最具原生态的一座无人岛。大嵩岛的原生态，碧海蓝天加上白色的细沙，葱葱郁郁的植被，各式各样的海洋生物以及美轮美奂的海岸线，是休闲度假放飞自我的绝佳圣地。岛上那些迷彩般的石头古厝、浪漫唯美的风车、独特的晚霞、星火斑斓的“蓝眼泪”等都是在大嵩岛的绝美风景，瞬息万变的云彩也是大嵩岛专属的。', '', '', '', '', '', '', '', '', '免费开放。', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1629, '海西双鱼岛', '国内首座大型离岸式生态人工岛——招商局漳州开发区双鱼岛，招商局集团预计投30亿元人民币造岛，漳州开发区将高起点规划、高标准建设，致力把双鱼岛建成以旅游度假、休闲体育、文化娱乐、商业居住为主的生态型岛屿。', '', '', '', '', '', '', '', '', '免费', 1);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1630, '月星环球港演艺空间', '游客可以倾听来自幽篁里古琴艺术中心的琴声，把玩宜兴紫砂壶，在敬华艺术展厅内欣赏名家大作，在海上旗袍艺术馆内逍遥，在城市记忆汇展内回味上海的历史。', '', '', '地铁3号线、4号线、13号线【金沙江路】站下。公交67、765等线路到【华东师范大学】下车，步行5分钟即可到达。', '', '', '', '', '', '免费', 0);
INSERT INTO detailscene (id, attract, overview, ticket, season, traffic, tip, address, phone, hours, website, scene, type) VALUES (1631, '厦门大桥', '厦门大桥是我国一座跨越海峡的公路大桥，由主桥，集美立交桥，高崎引道和自动化管理系统组成。主桥长2070米，桥面宽23.5米。始建于1987年10月1日，并于1991年12月19日正式通车。直通式集美立交桥桥北侧则有集美大学、华侨大学。游客乘车经过厦门大桥不仅可以领略大桥雄伟的气势，也能观赏到桥两边秀美的海岛风景。', '【厦门出发】集美+大嶝岛一日游（酒店接送+门票+午餐+导服）¥168¥68起查看报价浯屿岛海上田园+捞笼体验+五星酒店下午茶/一厝好戏一日游¥298¥298起查看报价厦门环岛旅游观光巴士一日游（高颜值 海岛游）¥60¥50起查看报价', '四季皆宜。', '公交1、乘坐122路、903路、959路至【龙舟池】站下，再步行约1.8公里。2、乘坐公交659路、951路至【高崎】站下，再步行约1.3公里即可。费用：由厦门岛内前往，则程票价2元；由厦门岛外前往，则单程票价1元。', '', '', '', '', '', '无需门票。', 4);
