package com.example.user.controller;

import com.example.user.entity.User;
import com.example.user.entity.User_roles;
import com.example.user.repository.UserRepository;
import com.example.user.repository.UserRolesReposiroty;
import com.example.user.service.SmtpMailSender;
import org.springframework.beans.factory.annotation.*;
import org.springframework.security.crypto.bcrypt.BCryptPasswordEncoder;
import org.springframework.security.oauth2.config.annotation.web.configuration.EnableResourceServer;
import org.springframework.web.bind.annotation.*;

import javax.mail.MessagingException;
import java.text.SimpleDateFormat;
import java.util.Date;
import java.util.List;

@RestController
@EnableResourceServer
public class UserController {
    @Autowired
    private UserRepository userRepository;

    @Autowired
    private UserRolesReposiroty userRolesReposiroty;

    @Autowired
    SmtpMailSender smtpMailSender;

    private String boypicture ="";

    private String girlpicture = "";

    @GetMapping("/users/id/{id}")
    public User getUser(@PathVariable Long id) { return userRepository.findById(id).get();}

    //tested
    @GetMapping("/users/username/{username}")
    public User getByUsername(@PathVariable String username){
        return userRepository.findUserByUsername(username);
    }

    //tested
    @RequestMapping("/un/signup")
    public String addUser(@RequestParam String username,@RequestParam String password,
                        @RequestParam String email){
        User uTest = userRepository.findUserByUsername(username);
        if (uTest != null) {
            return "username already exists";
        }

        User u = new User();
        u.setPassword(password);
        u.setUsername(username);
        u.setEmail(email);
        u.setProfile_picture(girlpicture);
        //获得当前日期
        Date d = new Date();
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
        String dateNowStr = sdf.format(d);
        u.setVipdate(java.sql.Date.valueOf(dateNowStr));

        User_roles user_roles = new User_roles();
        user_roles.setUsername(username);
        user_roles.setRole("ROLE_USER");
        userRolesReposiroty.save(user_roles);
        userRepository.save(u);
        return "register success";
    }

    //tested
    @GetMapping("/un/signin")
    public boolean signin(@RequestParam String username, @RequestParam String password){
        BCryptPasswordEncoder encoder = new BCryptPasswordEncoder();
        User u = userRepository.findUserByUsername(username);
        if(u == null){
            // do nothing. Waits to exit loop
        } else if(encoder.matches(password,u.getPassword())){
            return true;
        }

        System.out.println("null");
        return false;
    }

    //tested
    @RequestMapping("/users/userinfo/{id}")
    public User postUserInfo(@PathVariable(value = "id")Long id,
                             @RequestParam String sex,@RequestParam int age,
                             @RequestParam String address,@RequestParam String phone)
    {
        User u = userRepository.findById(id).get();
        u.setAddress(address);
        u.setAge(age);
        u.setSex(sex);
        u.setPhone(phone);
        return userRepository.save(u);
    }

    //tested
    @RequestMapping("/users/improvip/{id}")
    public User improPermission(@PathVariable(value = "id") Long id){
        User u = userRepository.findById(id).get();
        u.setVip(u.getVip()+1);
        return userRepository.save(u);
    }

    @RequestMapping("/un/refindId")
    public String refindpassword(@RequestParam String email) {
        User u = userRepository.findUserByEmail(email);
        if(u == null) {
            return "no such email";
        }else {
            //这里应该用验证用户邮箱的功能，现在未实现，先可以直接修改
            return u.getId().toString();
        }

    }

    @RequestMapping("/users/changepassword")
    public String changePassword(@RequestParam Long id,
                                 @RequestParam String oldPassword, @RequestParam String newPassword){
        User u = userRepository.findById(id).get();
        String tempOldPassword1 = u.getPassword();
        BCryptPasswordEncoder encoder = new BCryptPasswordEncoder();
        String tempOldPassword2 = encoder.encode(oldPassword);

        if (tempOldPassword1 != tempOldPassword2) {
            return "Password not match! Not successful!";
        }

        u.setPassword(newPassword);
        userRepository.save(u);

        return "success";
    }

    @RequestMapping("/admin/alluser")
    public List<User> alluser() {
        return (List<User>) userRepository.findAll();
    }

    @RequestMapping("/un/refindPassword")
    public String sendMail(@RequestParam String email) throws MessagingException {
        User u = userRepository.findUserByEmail(email);

        if(u == null) {
            return "no such email";
        }

        int n = 15;
        // chose a Character random from this String
        String AlphaNumericString = "ABCDEFGHIJKLMNOPQRSTUVWXYZ"
                + "0123456789"
                + "abcdefghijklmnopqrstuvxyz";

        // create StringBuffer size of AlphaNumericString
        StringBuilder sb = new StringBuilder(n);

        for (int i = 0; i < n; i++) {

            // generate a random number between
            // 0 to AlphaNumericString variable length
            int index = (int)(AlphaNumericString.length() * Math.random());

            // add Character one by one in end of sb
            sb.append(AlphaNumericString.charAt(index));
        }

        String tempPassword = sb.toString();

        // send email
        smtpMailSender.sendMail(email, "RESET PASSWORD",
                "This is your new password: " + tempPassword + "\nDo not share this with anyone\nPlease change your password after successful log in!");

        u.setPassword(tempPassword);
        userRepository.save(u);

        return "success";
    }
}