    @Transactional(isolation = Isolation.SERIALIZABLE)
    public String addUser(@RequestParam String username,@RequestParam String password,
                          @RequestParam String email, @RequestParam String phone, @RequestParam int gender) { // gender: 0 fo male, 1 for female
        User uTest1 = userRepository.findUserByUsername(username);
        if (uTest1 != null) {
            return "username already exists";
        }

        User uTest2 = userRepository.findUserByEmail(email);
        if (uTest2 != null) {
            return "email already exists";
        }

        User uTest3 = userRepository.findUserByPhone(phone);
        if (uTest3 != null) {
            return "phone number already exists";
        }

        User u = new User();
        u.setPassword(password);
        u.setUsername(username);
        u.setEmail(email);
        u.setPhone(phone);
        u.setGender(gender);

        if (gender == 1) {
            u.setProfile_picture(girlpicture);
        } else if (gender == 0) {
            u.setProfile_picture(boypicture);
        }

        //获得当前日期
        Date d = new Date();
        SimpleDateFormat sdf = new SimpleDateFormat("yyyy-MM-dd");
        String dateNowStr = sdf.format(d);
        u.setVipdate(java.sql.Date.valueOf(dateNowStr));

        // start new thread in background to improve response time
        Thread t = new Thread(){
            public void run(){
                System.out.println("test1");
                sendSignupEmail(email, username);
            }
        };
        t.start();

        User_roles user_roles = new User_roles();
        user_roles.setUsername(username);
        user_roles.setRole("ROLE_USER");

        userRolesRepository.save(user_roles);
        userRepository.save(u);

        return "register success";
    }

