    public void update(@RequestParam Long id, long c1, long c2, long c3, long c4, long c5) {
        User user = userRepository.findUserById(id);

        user.setC1(user.getC1() + c1);
        user.setC2(user.getC2() + c2);
        user.setC3(user.getC3() + c3);
        user.setC4(user.getC4() + c4);
        user.setC5(user.getC5() + c5);

        userRepository.save(user);
    }

    public String changePhoto(Long id, String photo) {
        User u = userRepository.findUserById(id);
        if(u==null){
            return "user id do not exists";
        }
        u.setProfile_picture(photo);
        userRepository.save(u);
        return photo;
    }

    public String vuesignin(String username,String password) {
        User_roles user_roles = userRolesRepository.findUser_rolesByUsername(username);
        if(!user_roles.getRole().equals("ROLE_ADMIN")){
            return "username or password wrong";
        }
        User user = userRepository.findUserByUsername(username);
        user.getPassword();
        BCryptPasswordEncoder encoder = new BCryptPasswordEncoder();
        if(encoder.matches(password, user.getPassword())){
            return "register success";
        }else{
            return "username or password wrong";
        }
    }
